document.addEventListener('DOMContentLoaded', function(){
	window.onscroll = function(){
		var div1 = document.getElementById('div1');
		var div2 = document.getElementById('div2');
		let div1Top = div1.offsetTop;
		let div1Bottom = div1Top + div1.offsetHeight;
		let div2Top = div2.offsetTop;
		let div2Bottom = div2Top + div2.offsetHeight;

		console.log('DIV 1');
		console.log('Top: ' + div1Top);
		console.log('Bottom: ' + div1Bottom);
		console.log('DIV 2');
		console.log('Top: ' + div2Top);
		console.log('Bottom: ' + div2Bottom);
	}
});






